import axios from 'axios';
import React , {useState} from "react";
const SentenceApp = () => {
    const [text, setText] = useState('');
    const [mapObject, setMapObject] = useState(new Map());
    const handleTextChange = (event) => {
        setText(event.target.value);
    };
    const handleSubmit = () => {
        console.log('entererd text ', text);
        const newMap = new Map(mapObject);
        newMap.set('inputText', text);
        setMapObject(newMap);
        
        const selectedValuesJSON = JSON.stringify(Object.fromEntries(newMap));
        console.log(selectedValuesJSON);

		var http = new XMLHttpRequest();
		var url = 'http://localhost:9091/description';
		http.open('POST', url, false);

		//Send the proper header information along with the request
		http.setRequestHeader('Content-type', 'application/json');

		http.onreadystatechange = function() {//Call a function when the state changes.
			if(http.readyState == 4 && http.status == 200) {
				alert(http.responseText);
			}
		}
		http.send();
    }
    
    return (
        <div>
            <div className="title-headings">
                <h3> Selection Application </h3> </div>
            <div>
                 <textarea
                    rows={5} cols={50} 
                    value={text}
                    onChange={handleTextChange}
                    placeholder="Enter your text here..."/>
            </div>
            <button onClick={handleSubmit}>Apply To Release.</button>
        </div>
    );
}
export default SentenceApp;
