import React , { useState }from "react";

function DropdownMenu({ 
    templateOptions,
    labelName }) {
    const [selectedOption, setSelectedOption] = useState(templateOptions);

    // const buildCi = ['Azure devops', 'Docker', 'github Actions', 'Jenkins']

    const handleOptionChange = (event) => {
        // setSelectedOption.push(event.target.value);
        // setSelectedOption( arr => [...arr, event.target.value]);
        // console.log("option value", selectedOption);
        console.log('option selected', event.target.value);
        const addingOption = [...selectedOption, event.target.value];
        setSelectedOption(addingOption);
        console.log('array selected', selectedOption);
    };

    return (
        <div>  
        <label htmlFor="dropdown">Select an option for {labelName}</label>
        <select id="dropdown" value={selectedOption} onChange={handleOptionChange}>
          {templateOptions.map((templateOption, index) => (
            <option key={index} value={templateOption}>
              {templateOption}
            </option>
          ))}
        </select>
      </div>

    )
}

export default DropdownMenu;
