import React , { useState }from "react";

function DropdownMenu() {
    const [selectedOption, setSelectedOption] = useState('');
    const workManagement = ['Agility', 'Jira', 'Servicenow'];
    const SCM = ['Bitbucket', 'Git', 'Teamforge'];
    // const buildCi = ['Azure devops', 'Docker', 'github Actions', 'Jenkins']

    const handleOptionChange = (event) => {
        setSelectedOption(event.target.value);
    };

    return (
    <div>
      <h3>Build your template .....</h3>
      <label htmlFor="workManagement">Select workManagement:</label>
      <select id="workdropdown" value={selectedOption} onChange={handleOptionChange}>
        {workManagement.map((wm, index) => (
          <option key={index} value={wm}>
            {wm}
          </option>
        ))}
      </select> 
      <br></br>

      <label htmlFor="scm">Select SCM:</label>
      <select id="scmdropdown" value={selectedOption} onChange={handleOptionChange}>
        {SCM.map((scm, index) => (
          <option key={index} value={scm}>
            {scm}
          </option>
        ))}
      </select> 
    </div>

    )
}

export default DropdownMenu;
