import React from "react";
import ReactDOM from "react-dom";
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import imageSrc from './selection.png';
import sentenceImg from './sentence.png';
import Selection from "./selection.js";
//import App from './index.js';

function App() {
    const selectionAppln = "Selection";
    const sentenceAppln = "sentence";
    // const handleClick = () => {
        // load
    //}
    return (
        <div className="main-appln">
            <h3>Choose your Application</h3>
            <Router>
            <Switch>
                <Route exact path="/">
                     <img src={imageSrc} alt="Image1" className="image" />
                     <img src={sentenceImg} alt="Image1" className="image" />
                </Route>
                <Route path="/" component={Selection}>
                    <img src={imageSrc} alt="Image1" className="image" />
                    <Link to="/dropdown">{selectionAppln}</Link>
                </Route>

                <Route path="/selection">
                    <img src={sentenceImg} alt="Image1" className="image" />
                    <Link to="/selection">{sentenceAppln} </Link>
                </Route>
            </Switch>
            </Router>
        </div>
    );
}
ReactDOM.render(<App/>, document.querySelector("#root"));
// export default MainAppln;
