import React , { useState }from "react";

function DropdownMenu({ 
    templateOptions,
    labelName,
    placeholder }) {
    const [selectedOption, setSelectedOption] = useState([]);

    // const buildCi = ['Azure devops', 'Docker', 'github Actions', 'Jenkins']

    const handleOptionChange = (event) => {
        console.log('option selected', labelName, 'evnt value', event.target.value);
        setSelectedOption(event.target.value);
    };

    return (
        <div>  
        <label htmlFor="dropdown">Select an option for {labelName}</label>
        <select id="dropdown" value={selectedOption} onChange={handleOptionChange}>
        <option value="" disabled hidden>
          {placeholder}
        </option>

        {templateOptions.map((templateOption, index) => (
        <option key={index} value={templateOption}> {templateOption}
        </option>
        ))}
        </select>
      </div>

    )
}

export default DropdownMenu;
